import React, { Component } from 'react';
import NewsItem from './Newsitem';
import PropTypes from 'prop-types'


export default class News extends Component {
  static defaultProps = {
    country : 'us',
    pageSize : 8,
    catagory : 'general'
  }
  static propTypes = {
    country : PropTypes.string,
    pageSize : PropTypes.number,
    catagory : PropTypes.string
  }
  constructor() {
    super();
    console.log("hello I am a constructor") 
    this.state = {
      articles: [],
      loading: false
    };
  }
  
  componentDidMount() {
    fetch(`https://newsapi.org/v2/top-headlines?category=${this.props.catagory}&country=${this.props.country}&pageSize=${this.props.pageSize}&apiKey=5b5c3e5b65214561a06414f09b414f48`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Error fetching data');
        }
        return res.json();
      })
      .then((data) => { // Store fetched data in component state
        this.setState({
          articles: data.articles,
          loading: false
        });
      })
      .catch((error) => {
        console.log("error in fetching")
        this.setState({ loading: false });
      });
  }
  handleprevbutton=async()=>{
    console.log("previos button clicked")
    fetch(`https://newsapi.org/v2/top-headlines?category=${this.props.catagory}&country=${this.props.country}&pageSize=${this.props.pageSize}&page=${this.state.page-1}&apiKey=5b5c3e5b65214561a06414f09b414f48`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Error fetching data');
        }
        return res.json();
      })
      .then((data) => { // Store fetched data in component state
        this.setState({
          articles: data.articles,
          page:this.state.page-1,
          loading: false
        });
      })
      .catch((error) => {
        console.log("error in fetching")
        this.setState({ loading: false });
      });
  }
  handlenextbutton=async()=>{
    console.log("next button clicked")
    fetch(`https://newsapi.org/v2/top-headlines?category=${this.props.catagory}&country=${this.props.country}&pageSize=${this.props.pageSize}&page=${this.state.page+1}&apiKey=5b5c3e5b65214561a06414f09b414f48`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Error fetching data');
        }
        return res.json();
      })
      .then((data) => { // Store fetched data in component state
        this.setState({
          articles: data.articles,
          page:this.state.page+1,
          loading: false
        });
      })
      .catch((error) => {
        console.log("error in fetching")
        this.setState({ loading: false });
      });
      this.setState({
        page:this.state.page+1
      })
  }

  render() {
    return (
      <div style={{backgroundImage:'https://elements-video-cover-images-0.imgix.net/files/11443e38-f9e9-4f6e-9b34-be17f13e22a9/inline_image_preview.jpg?auto=compress%2Cformat&fit=min&h=394&w=700&s=669b565342a1f231924f3a1a76a38690'}}>
        <div className="container text-center">
          <div className="row my-5">
          {this.state.articles.map((element) => (
            <div className="col" key={element.url}>
              <NewsItem
                title={element.title ? element.title.slice(0, 45) : "No title"}
                description={element.description ? element.description.slice(0, 88) : "No description"}
                imageUrl={element.urlToImage}
                newsUrl={element.url}
              />
            </div>
          ))}
          </div>
          <div class='d-flex justify-content-between'>
            <button disabled={this.state.page<=1} type="button" onClick={this.handleprevbutton} class="btn btn-dark">&larr; Left</button>
            <button type="button" onClick={this.handlenextbutton} class="btn btn-dark">Right &rarr;</button>
          </div>
        </div>
      </div>
    );
  }
}
