import React, { Component } from 'react'
import PropTypes from 'prop-types'

export default class detail extends Component {
    static defaultProps = {
        country : 'us',
        pageSize : 8,
        catagory : 'general'
      }
      static propTypes = {
        country : PropTypes.string,
        pageSize : PropTypes.number,
        catagory : PropTypes.string
      }
      constructor() {
        super();
        console.log("hello I am a constructor") 
        this.state = {
          articles: [],
          loading: false
        };
      }
      
      componentDidMount() {
        fetch(`https://newsapi.org/v2/top-headlines?category=${this.props.catagory}&country=${this.props.country}&pageSize=${this.props.pageSize}&apiKey=5b5c3e5b65214561a06414f09b414f48`)
          .then((res) => {
            if (!res.ok) {
              throw new Error('Error fetching data');
            }
            return res.json();
          })
          .then((data) => { // Store fetched data in component state
            this.setState({
              articles: data.articles,
              loading: false
            });
          })
          .catch((error) => {
            console.log("error in fetching")
            this.setState({ loading: false });
          });
      }
     
  render() {
    return (
      <div>
        
      </div>
    )
  }
}
