import React, { Component } from 'react';
import NewsItem from './Newsitem';
import PropTypes from 'prop-types'

export default class News extends Component {
  static defaultProps = {
    country: 'us',
    pageSize: 8,
    category: 'general'
  }

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string
  }

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1 // Initialize page in state
    };
  }

  componentDidMount() {
    this.fetchArticles();
  }

  fetchArticles = () => {
    const { category, country, pageSize } = this.props;
    const { page } = this.state;

    fetch(`https://newsapi.org/v2/top-headlines?category=${category}&country=${country}&pageSize=${pageSize}&page=${page}&apiKey=YOUR_API_KEY`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Error fetching data');
        }
        return res.json();
      })
      .then((data) => {
        this.setState({
          articles: data.articles,
          loading: false
        });
      })
      .catch((error) => {
        console.log("error in fetching", error);
        this.setState({ loading: false });
      });
  }

  handlePrevButton = () => {
    this.setState(prevState => ({ page: prevState.page - 1 }), this.fetchArticles);
  }

  handleNextButton = () => {
    this.setState(prevState => ({ page: prevState.page + 1 }), this.fetchArticles);
  }

  render() {
    const { articles } = this.state;

    return (
      <div style={{ background: 'url(https://elements-video-cover-images-0.imgix.net/files/11443e38-f9e9-4f6e-9b34-be17f13e22a9/inline_image_preview.jpg?auto=compress%2Cformat&fit=min&h=394&w=700&s=669b565342a1f231924f3a1a76a38690) no-repeat center center/cover' }}>
        <div className="container text-center">
          <div className="row my-5">
            {articles.map((element, index) => (
              <div className="col" key={index}>
                <NewsItem
                  title={element.title ? element.title.slice(0, 45) : "No title"}
                  description={element.description ? element.description.slice(0, 88) : "No description"}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            ))}
          </div>
          <div className='d-flex justify-content-between'>
            <button disabled={this.state.page <= 1} type="button" onClick={this.handlePrevButton} className="btn btn-dark">&larr; Left</button>
            <button type="button" onClick={this.handleNextButton} className="btn btn-dark">Right &rarr;</button>
          </div>
        </div>
      </div>
    );
  }
}
