import React, { Component } from 'react'

export default class detail extends Component {
  render() {
    const { title, imageUrl,author,content } = this.props;
    return (
      <div style={{marginTop: '80px'}}>
        <div style={{textAlign:'center'}} className='title'><h1>{title}</h1></div>
        <div className='image'><img src={imageUrl} alt="Image not available" /></div>
        <div className='content'>
            {content}
        </div>
      </div>
    )
  }
}
