import React from 'react';
import { useLocation } from 'react-router-dom';

const Detail = () => {
  const location = useLocation();
  const { title, content, imageUrl, author } = location.state;
  console.log('Location State:', location.state); // Check the logged state in the browser's console

  return (
    <div>
      <h1 style={{marginTop:'100px',textAlign:'center'}}>"{title}"</h1>
      <img style={{height:'400px',width:'500px',margin:'auto'}} src={imageUrl} alt="" />
      <p>{content}</p>
      <h5>{author}</h5>
    </div>
  );
};

export default Detail;
